list-view-config.query-select:
PREFIX blocal: <http://vivo.brown.edu/ontology/vivo-brown/> PREFIX vivo: <http://vivoweb.org/ontology/core#> PREFIX owl: <http://www.w3.org/2002/07/owl#> PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX bcite: <http://vivo.brown.edu/ontology/citation#> PREFIX foaf: <http://xmlns.com/foaf/0.1/>
SELECT DISTINCT ?citation ?label ?dateTime ?authorList ?venue ?venueLabel ?doi ?pmid ?firstName ?lastName
WHERE {
?subject ?property ?citation . ?citation rdfs:label ?label . ?citation bcite:date ?dateTime . ?subject foaf:firstName ?firstName. ?subject foaf:lastName ?lastName .
OPTIONAL { ?citation bcite:authorList ?authorList . } OPTIONAL { ?citation bcite:hasVenue ?venue . ?venue rdfs:label ?venueLabel . } OPTIONAL { ?citation bcite:doi ?doi . } OPTIONAL { ?citation bcite:pmid ?pmid . }
FILTER ( bound(?citation) )
} ORDER BY DESC(?dateTime) ?label

list-view-config.query-construct:
PREFIX vivo: <http://vivoweb.org/ontology/core#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX bcite: <http://vivo.brown.edu/ontology/citation#>
PREFIX foaf: <http://xmlns.com/foaf/0.1/>

CONSTRUCT {
    ?subject ?property ?citation .
    ?citation rdfs:label ?label .
    ?citation bcite:hasVenue ?venue .
    ?venue rdfs:label ?venueLabel .
    ?citation bcite:date ?dateTime .
    ?citation bcite:authorList ?authorList .
    ?citation bcite:doi ?doi .
    ?citation bcite:pmid ?pmid .
    ?subject foaf:firstName ?firstName.
    ?subject foaf:lastName ?lastName .
} WHERE {
    {
        ?subject ?property ?citation .
        ?citation rdfs:label ?label .
        ?subject foaf:firstName ?firstName.
        ?subject foaf:lastName ?lastName .
    }
    UNION {
        ?subject ?property ?citation .
        ?citation bcite:authorList ?authorList .
    }            
    UNION {
        ?subject ?property ?citation .
        ?citation bcite:date ?dateTime .            
    }
    UNION {
        ?subject ?property ?citation .
        ?citation bcite:hasVenue ?venue .
        ?venue rdfs:label ?venueLabel .
    }
    UNION {
        ?subject ?property ?citation .
        ?citation bcite:doi ?doi .
    }
    UNION {
        ?subject ?property ?citation .
        ?citation bcite:pmid ?pmid .
    }
}
