list-view-config.query-select:
PREFIX blocal: <http://vivo.brown.edu/ontology/vivo-brown/> PREFIX vivo: <http://vivoweb.org/ontology/core#> PREFIX owl: <http://www.w3.org/2002/07/owl#> PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
SELECT DISTINCT ?faculty ?facLabel ?termList
WHERE {
?subject ?property ?faculty .
OPTIONAL { ?faculty rdfs:label ?facLabel . }
?faculty blocal:fullTermList ?termList .
FILTER ( bound(?faculty) )
} ORDER BY ?facLabel

list-view-config.query-construct:
PREFIX vivo: <http://vivoweb.org/ontology/core#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX blocal: <http://vivo.brown.edu/ontology/vivo-brown/>

CONSTRUCT {
    ?subject ?property ?faculty .
    ?faculty rdfs:label ?facLabel .
} WHERE {

    {
        ?subject ?property ?course .
        ?course vivo:realizedRole ?role .
        ?role vivo:teacherRoleOf ?faculty .
    } UNION {
        ?subject ?property ?course .
        ?course vivo:realizedRole ?role .
        ?role vivo:teacherRoleOf ?faculty .
        ?faculty rdfs:label ?facLabel .
    }
}

PREFIX vivo: <http://vivoweb.org/ontology/core#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX blocal: <http://vivo.brown.edu/ontology/vivo-brown/>

CONSTRUCT {?faculty blocal:fullTermList ?termList .}
WHERE {
    SELECT ?faculty (GROUP_CONCAT(?termLabel; SEPARATOR = ", ") as ?termList)
    WHERE {
        SELECT ?faculty ?termLabel ?startValue
        WHERE { 
            ?subject ?property ?course .
            ?course vivo:realizedRole ?role .
            ?role vivo:teacherRoleOf ?faculty .
            ?role vivo:dateTimeInterval ?dateTimeInterval .
            ?dateTimeInterval vivo:start ?startDate .
            ?dateTimeInterval rdfs:label ?termLabel . 
            ?startDate vivo:dateTime ?startValue

        } ORDER BY ?startValue

    } GROUP BY ?faculty
}
