list-view-config.query-select:
PREFIX afn: <http://jena.hpl.hp.com/ARQ/function#> PREFIX blocal: <http://vivo.brown.edu/ontology/vivo-brown/> PREFIX vivo: <http://vivoweb.org/ontology/core#> PREFIX owl: <http://www.w3.org/2002/07/owl#> PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#> PREFIX event: <http://purl.org/NET/c4dm/event.owl#>
SELECT DISTINCT ?subclass ?course ?termList ?faculty ?facLabel
WHERE {
?subject ?property ?course .
OPTIONAL { ?course vivo:realizedRole ?role . ?role vivo:teacherRoleOf ?faculty . ?faculty rdfs:label ?facLabel . }
OPTIONAL {
?course vitro:mostSpecificType ?subclass . ?subclass rdfs:subClassOf event:Event .
}
?course blocal:sampleCourse ?faculty . ?faculty blocal:fullTermList ?termList .
FILTER ( bound(?course) )
} ORDER BY ?subclass ?facLabel

list-view-config.query-construct:
PREFIX vivo: <http://vivoweb.org/ontology/core#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX blocal: <http://vivo.brown.edu/ontology/vivo-brown/>

CONSTRUCT {?sampleCourse blocal:sampleCourse ?faculty .}
WHERE {
    SELECT ?faculty (SAMPLE(?course) AS ?sampleCourse)
    WHERE {
        ?subject ?property ?course .
        ?course vivo:realizedRole ?role .
        ?role vivo:teacherRoleOf ?faculty 

    } GROUP BY ?faculty
}

PREFIX vivo: <http://vivoweb.org/ontology/core#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX blocal: <http://vivo.brown.edu/ontology/vivo-brown/>

CONSTRUCT {
    ?subject ?property ?course .
    ?course vivo:realizedRole ?role .
    ?role vivo:teacherRoleOf ?faculty .
    ?faculty rdfs:label ?facLabel .
} WHERE {

    {
        ?subject ?property ?course .
        ?course vivo:realizedRole ?role .
        ?role vivo:teacherRoleOf ?faculty .
    } UNION {
        ?subject ?property ?course .
        ?course vivo:realizedRole ?role .
        ?role vivo:teacherRoleOf ?faculty .
        ?faculty rdfs:label ?facLabel .
    }
}

PREFIX vivo: <http://vivoweb.org/ontology/core#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX blocal: <http://vivo.brown.edu/ontology/vivo-brown/>

CONSTRUCT {?faculty blocal:fullTermList ?termList .}
WHERE {
    SELECT ?faculty (GROUP_CONCAT(?termLabel; SEPARATOR = ", ") as ?termList)
    WHERE {
        SELECT ?faculty ?termLabel ?startValue
        WHERE { 
            ?subject ?property ?course .
            ?course vivo:realizedRole ?role .
            ?role vivo:teacherRoleOf ?faculty .
            ?role vivo:dateTimeInterval ?dateTimeInterval .
            ?dateTimeInterval vivo:start ?startDate .
            ?dateTimeInterval rdfs:label ?termLabel . 
            ?startDate vivo:dateTime ?startValue

        } ORDER BY ?startValue

    } GROUP BY ?faculty
}
