list-view-config.query-select:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX vivo: <http://vivoweb.org/ontology/core#> PREFIX afn: <http://jena.hpl.hp.com/ARQ/function#> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#> PREFIX blocal: <http://vivo.brown.edu/ontology/vivo-brown/>
SELECT DISTINCT ?subclass ?position ?positionTitle ?person ?personName ?preferredTitle ?dateTimeStart ?dateTimeEnd ?hideThis ?honoraryTitle WHERE { ?subject ?property ?position . ?position a blocal:FisPosition .
OPTIONAL { ?position vivo:positionForPerson ?person . ?person rdfs:label ?personName . OPTIONAL { ?person blocal:hasHonorific ?honoraryTitle } }
OPTIONAL { ?position vitro:mostSpecificType ?subclass . ?subclass rdfs:subClassOf vivo:Position }
OPTIONAL { ?position rdfs:label ?positionTitle } OPTIONAL { ?position vivo:hideFromDisplay ?hideThis } OPTIONAL { ?position vivo:dateTimeInterval ?dateTimeInterval OPTIONAL { ?dateTimeInterval vivo:start ?dateTimeStartValue . ?dateTimeStartValue vivo:dateTime ?dateTimeStart } OPTIONAL { ?dateTimeInterval vivo:end ?dateTimeEndValue . ?dateTimeEndValue vivo:dateTime ?dateTimeEnd } # Get current positions only: end date is either null or not in the past } # NOT EXISTS { ?position vivo:hideFromDisplay ?hideThis } FILTER ( !bound(?dateTimeEnd) || afn:substring(str(?dateTimeEnd), 0, 4) >= afn:substring(str(afn:now()), 0, 4) ) #Remove honorofic positions so they don't appear twice MINUS { ?position a blocal:Honorific }
FILTER ( bound(?person) )
} ORDER BY ?subclass ?personName

list-view-config.query-construct:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>  
PREFIX vivo: <http://vivoweb.org/ontology/core#>
PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>  
CONSTRUCT {
    ?subject ?property ?position .
    ?position vitro:mostSpecificType ?subclass .
    ?subclass rdfs:subClassOf vivo:Position 
} WHERE {
    ?subject ?property ?position .
    ?position vitro:mostSpecificType ?subclass .
    ?subclass rdfs:subClassOf vivo:Position 
}

PREFIX vivo: <http://vivoweb.org/ontology/core#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> 
PREFIX blocal: <http://vivo.brown.edu/ontology/vivo-brown/>       
CONSTRUCT { 
    ?subject ?property ?position .
    ?position ?positionProperty ?positionValue .
    ?person rdfs:label ?personName  .
    ?person blocal:hasHonorific ?honoraryTitle .
} WHERE {
    {
       ?subject ?property ?position .
       ?position a blocal:FisPosition .
    } UNION {
       ?subject ?property ?position .
       ?position ?positionProperty ?positionValue 
    } UNION {
       ?subject ?property ?position .
       ?position vivo:positionForPerson ?person .
       ?person rdfs:label ?personName   
    } 
    UNION {
        ?subject ?property ?position .
        ?position a blocal:Honorific .
        ?position vivo:positionForPerson ?person .
        ?position rdfs:label ?honoraryTitle .
    }
}

PREFIX vivo: <http://vivoweb.org/ontology/core#>   
CONSTRUCT { 
    ?subject ?property ?position .  
    ?position vivo:dateTimeInterval ?dateTimeInterval .
    ?dateTimeInterval vivo:start ?dateTimeStartValue .
    ?dateTimeStartValue vivo:dateTime ?dateTimeStart 
} WHERE {
    ?subject ?property ?position .  
    ?position vivo:dateTimeInterval ?dateTimeInterval .
    ?dateTimeInterval vivo:start ?dateTimeStartValue .
    ?dateTimeStartValue vivo:dateTime ?dateTimeStart 
}

PREFIX vivo: <http://vivoweb.org/ontology/core#>    
CONSTRUCT { 
    ?subject ?property ?position .  
    ?position vivo:dateTimeInterval ?dateTimeInterval .
    ?dateTimeInterval vivo:end ?dateTimeEndValue .
    ?dateTimeEndValue vivo:dateTime ?dateTimeEnd 
} WHERE {
    ?subject ?property ?position .  
    ?position vivo:dateTimeInterval ?dateTimeInterval .
    ?dateTimeInterval vivo:end ?dateTimeEndValue .
    ?dateTimeEndValue vivo:dateTime ?dateTimeEnd 
}
