list-view-config.query-select:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX vivo: <http://vivoweb.org/ontology/core#> PREFIX afn: <http://jena.hpl.hp.com/ARQ/function#> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#> PREFIX blocal: <http://vivo.brown.edu/ontology/vivo-brown/>
#TL: Brown data currently doesn't contain dates for positions. Removing to make query simpler. SELECT DISTINCT ?subclass ?position ?positionTitle ?person ?personName ?hideThis WHERE { ?subject ?property ?position . ?position vivo:positionForPerson ?person . ?person rdfs:label ?personName . ?position blocal:positionLabel ?positionTitle . OPTIONAL { ?position vivo:hideFromDisplay ?hideThis }
OPTIONAL { ?position vitro:mostSpecificType ?subclass . ?subclass rdfs:subClassOf vivo:Position }
FILTER ( bound(?person) )
} ORDER BY ?subclass ?personName

list-view-config.query-construct:
PREFIX vivo: <http://vivoweb.org/ontology/core#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX blocal: <http://vivo.brown.edu/ontology/vivo-brown/>
#TL: This handles faculty with multiple positions in the same department. Titles will be combined for display.

CONSTRUCT {
  ?tempPosition blocal:positionLabel ?positionTitle .
}
WHERE {
  SELECT ?person (GROUP_CONCAT(?title; SEPARATOR = ", ") as ?positionTitle) (SAMPLE(?position) as ?tempPosition)
  WHERE {
    ?subject ?property ?position .
    ?position vivo:positionForPerson ?person .
    ?position rdfs:label ?title .
  }
  GROUP BY ?person
}

PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX vivo: <http://vivoweb.org/ontology/core#>
PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>
CONSTRUCT {
  ?subject ?property ?position .
  ?position vitro:mostSpecificType ?subclass .
  ?subclass rdfs:subClassOf vivo:Position
} WHERE {
  ?subject ?property ?position .
  ?position vitro:mostSpecificType ?subclass .
  ?subclass rdfs:subClassOf vivo:Position
}

PREFIX vivo: <http://vivoweb.org/ontology/core#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX blocal: <http://vivo.brown.edu/ontology/vivo-brown/>
CONSTRUCT {
    ?subject ?property ?position .
    ?position ?positionProperty ?positionValue .
    ?person rdfs:label ?personName  .
} WHERE {
    {
       ?subject ?property ?position .
       ?position a blocal:FisPosition .
    } UNION {
       ?subject ?property ?position .
       ?position ?positionProperty ?positionValue
    } UNION {
       ?subject ?property ?position .
       ?position vivo:positionForPerson ?person .
       ?person rdfs:label ?personName
    }
}
