list-view-config.query-select:
PREFIX afn: <http://jena.hpl.hp.com/ARQ/function#>
PREFIX blocal: <http://vivo.brown.edu/ontology/vivo-brown/>
PREFIX vivo: <http://vivoweb.org/ontology/core#>
PREFIX owl: <http://www.w3.org/2002/07/owl#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>
PREFIX event: <http://purl.org/NET/c4dm/event.owl#>

SELECT DISTINCT ?subclass
                ?role
                ?roleLabel
                ?sectionCount
                ?course
                ?courseLabel
                ?canon
                ?canonLabel
                ?dateTimeInterval
                ?termLabel
                ?startValue

    WHERE {

        ?subject ?property ?role

        OPTIONAL { ?role rdfs:label ?roleLabel }

        OPTIONAL { ?role blocal:sectionsTaught ?sectionCount }

        OPTIONAL { ?role vivo:roleRealizedIn ?course 

                    OPTIONAL { ?course rdfs:label ?courseLabel }

                    OPTIONAL { ?canon a blocal:CanonicalCourse .
                               OPTIONAL { ?canon rdfs:label ?canonLabel }

                               {
                                   ?course blocal:departmenOfferingOf ?canon
                               } UNION {
                                   ?canon blocal:hasDepartmentOffering ?course
                               }
                    }



                    ?course vitro:mostSpecificType ?subclass .
                    ?subclass rdfs:subClassOf event:Event 


        }

        OPTIONAL { ?role vivo:dateTimeInterval ?dateTimeInterval

                   OPTIONAL { ?dateTimeInterval a vivo:AcademicTerm .
                              ?dateTimeInterval rdfs:label ?termLabel
                   }
                   OPTIONAL { ?dateTimeInterval a vivo:AcademicTerm .
                              ?dateTimeInterval vivo:start ?startDate .
                              ?startDate vivo:dateTime ?startValue .
                    }
        }


        FILTER ( bound(?course) )


    } ORDER BY ?subclass DESC(?startValue) ?canonLabel

list-view-config.query-construct:
PREFIX vivo: <http://vivoweb.org/ontology/core#> PREFIX owl: <http://www.w3.org/2002/07/owl#> PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#> PREFIX event: <http://purl.org/NET/c4dm/event.owl#>
CONSTRUCT { ?subject ?property ?role . ?role vivo:roleRealizedIn ?course . ?course vitro:mostSpecificType ?subclass . ?subclass rdfs:subClassOf event:Event . } WHERE {
?subject ?property ?role . ?role vivo:roleRealizedIn ?course . ?course vitro:mostSpecificType ?subclass . ?subclass rdfs:subClassOf event:Event . }

PREFIX vivo: <http://vivoweb.org/ontology/core#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>

CONSTRUCT {
    ?subject ?property ?role .
    ?role ?roleProperty ?roleValue .
    ?course rdfs:label ?courseLabel
} WHERE {
    {
        ?subject ?property ?role .
    } UNION {
        ?subject ?property ?role .
        ?role ?roleProperty ?roleValue .
    } UNION {
        ?subject ?property ?role .
        ?role vivo:roleRealizedIn ?course .
        ?course rdfs:label ?courseLabel
    }
}

PREFIX vivo: <http://vivoweb.org/ontology/core#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX blocal: <http://vivo.brown.edu/ontology/vivo-brown/>

CONSTRUCT {
    ?subject ?property ?role .
    ?canon a blocal:CanonicalCourse .
    ?course blocal:departmentOfferingOf ?canon .
    ?canon blocal:hasDepartmentOffering ?course .
    ?canon rdfs:label ?canonLabel 
} WHERE {

    {    
        ?subject ?property ?role .
        ?role vivo:roleRealizedIn ?course .
        ?course blocal:departmentOfferingOf ?canon .
        ?canon a blocal:CanonicalCourse .
    } UNION {
        ?subject ?property ?role .
        ?role vivo:roleRealizedIn ?course .
        ?course blocal:departmentOfferingOf ?canon .
        ?canon a blocal:CanonicalCourse .
        ?canon rdfs:label ?canonLabel .
    } UNION {
        ?subject ?property ?role .
        ?role vivo:roleRealizedIn ?course .
        ?canon blocal:hasDepartmentOffering ?course .
        ?canon a blocal:CanonicalCourse .            
    } UNION {
        ?subject ?property ?role .
        ?role vivo:roleRealizedIn ?course .
        ?canon blocal:hasDepartmentOffering ?course .
        ?canon a blocal:CanonicalCourse .
        ?canon rdfs:label ?canonLabel .
    }
}

PREFIX vivo: <http://vivoweb.org/ontology/core#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>

CONSTRUCT {
    ?subject ?property ?role .
    ?role vivo:dateTimeInterval ?dateTimeInterval .
    ?dateTimeInterval a vivo:AcademicTerm .
    ?dateTimeInterval rdfs:label ?termLabel .
    ?dateTimeInterval vivo:start ?startDate .
    ?startDate vivo:dateTime ?startValue
} WHERE {

    {
        ?subject ?property ?role .
        ?role vivo:dateTimeInterval ?dateTimeInterval .
        ?dateTimeInterval a vivo:AcademicTerm .
    } UNION {
        ?subject ?property ?role .
        ?role vivo:dateTimeInterval ?dateTimeInterval .
        ?dateTimeInterval a vivo:AcademicTerm .
        ?dateTimeInterval rdfs:label ?termLabel 
    } UNION {
        ?subject ?property ?role .
        ?role vivo:dateTimeInterval ?dateTimeInterval .
        ?dateTimeInterval a vivo:AcademicTerm .
        ?dateTimeInterval vivo:start ?startDate .
        ?startDate vivo:dateTime ?startValue .
    }
}
