list-view-config.query-select:
PREFIX blocal: <http://vivo.brown.edu/ontology/vivo-brown/> PREFIX vivo: <http://vivoweb.org/ontology/core#> PREFIX owl: <http://www.w3.org/2002/07/owl#> PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
SELECT DISTINCT ?subclass ?cross ?crossLabel ?courseNumber ?sbjCode
WHERE {
?subject ?property ?cross . ?cross a blocal:CanonicalCourse .
OPTIONAL { ?cross rdfs:label ?crossLabel .} OPTIONAL { ?cross blocal:hasCourseNumber ?courseNumber . ?cross blocal:hasSubjectCode ?sbjCode .}
OPTIONAL { ?cross a ?subclass . ?subclass vitro:inClassGroup ?classgroup . }

list-view-config.query-construct:
PREFIX vivo: <http://vivoweb.org/ontology/core#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX blocal: <http://vivo.brown.edu/ontology/vivo-brown/>

CONSTRUCT {
    ?subject ?property ?cross .
    ?cross a blocal:CanonicalCourse .
    ?cross rdfs:label ?crossLabel .
    ?cross blocal:hasCourseNumber ?courseNumber .
    ?cross blocal:hasSubjectCode ?sbjCode .
    ?cross a ?subclass .
    ?subclass vitro:inClassGroup ?classgroup .
} WHERE {
    {    
        ?subject ?property ?cross .
        ?cross a blocal:CanonicalCourse .
    } UNION {
        ?subject ?property ?cross .
        ?cross a blocal:CanonicalCourse .
        ?cross rdfs:label ?crossLabel .
    } UNION {
        ?subject ?property ?cross .
        ?cross a blocal:CanonicalCourse .
        ?cross blocal:hasCourseNumber ?courseNumber .
        ?cross blocal:hasSubjectCode ?sbjCode .
    } UNION {
        ?subject ?property ?cross .
        ?cross a ?subclass .
        ?subclass vitro:inClassGroup ?classgroup .
    }
}
